	private static Factory f = TemplateTestCore.getFactory();

	public static Alphabet getDefaultAlphabet() {
		return f.getAlphabetInstance(Arrays.asList('a', 'b', 'c', 'd', 'e', 'f', 'g', 'h', 'i', 'j', 'k', 'l', 'm', 'n', 'o', 'p', 'q',
				'r', 's', 't', 'u', 'v', 'w', 'x', 'y', 'z'));
	}

	public static Alphabet getMixedDefaultAlphabet() {
		return f.getAlphabetInstance(Arrays.asList('m', 'a', 'g', 'c', 'j', 'f', 't', 'k', 'e', 'y', 'n', 'h', 'i', 'p', 'l', 'q', 'r',
				's', 'o', 'u', 'v', 'w', 'x', 'z', 'd', 'b'));
	}

	public static Alphabet getReversedDefaultAlphabet() {
		return f.getAlphabetInstance(Arrays.asList('z', 'y', 'x', 'w', 'v', 'u', 't', 's', 'r', 'q', 'p', 'o', 'n', 'm', 'l', 'k', 'j',
				'i', 'h', 'g', 'f', 'e', 'd', 'c', 'b', 'a'));
	}

	public static Alphabet getMinimalAlphabet() {
		return f.getAlphabetInstance(Arrays.asList('a', 'b', 'c', 'd'));
	}

	public static Alphabet getReversedMinimalAlphabet() {
		return f.getAlphabetInstance(Arrays.asList('d', 'c', 'b', 'a'));
	}

	public static int countDifferences(char[] a, char[] b) {
		int cnt = 0;

		if (a.length != b.length)
			throw new IllegalArgumentException("expected two arrays of same size");

		for (int i = 0; i < a.length; i++) {
			if (a[i] != b[i])
				cnt++;
		}

		return cnt;
	}

	public static Alphabet getAlphabetFrom(char[] alphChars) {
		List<Character> chars = new ArrayList<>();
		for (char c : alphChars)
			chars.add(c);
		return f.getAlphabetInstance(chars);
	}
